mapper.select:
SELECT category_id, name, description, use_yn, create_date, modify_date FROM category WHERE 1=1
AND category_id = #{categoryId}
AND name LIKE CONCAT('%', #{name}, '%')
AND use_yn = #{useYn}
ORDER BY create_date DESC LIMIT #{paging.startRecordIndex}, #{paging.pageSize}

SELECT COUNT(*) FROM category WHERE 1=1
AND category_id = #{categoryId}
AND name LIKE CONCAT('%', #{name}, '%')
AND use_yn = #{useYn}

mapper.insert:
INSERT INTO category (
    name, 
    description, 
    use_yn
) 
VALUES (
    #{name}, 
    #{description}, 
    #{useYn}
);
